// Generate a random product description for placeholder purposes
const generateDescription = (productName: string): string => {
  const commonPhrases = [
    "A revolutionary formula designed to",
    "Expertly crafted to address concerns like",
    "Perfect for daily use, this product helps to",
    "Harnessing the power of key ingredients, it targets",
    "A dermatologist-recommended solution for achieving"
  ];
  const commonBenefits = [
    "reveal a brighter, more even-toned complexion.",
    "reduce the appearance of fine lines and wrinkles.",
    "provide intense hydration and support the skin barrier.",
    "control excess oil and minimize the look of pores.",
    "soothe sensitive skin and reduce redness.",
    "fade dark spots and hyperpigmentation."
  ];

  const randomPhrase = commonPhrases[Math.floor(Math.random() * commonPhrases.length)];
  const randomBenefit = commonBenefits[Math.floor(Math.random() * commonBenefits.length)];

  return `${productName} is a premium skincare product. ${randomPhrase} ${randomBenefit}`;
};

// Generate suitableFor tags based on product name keywords
const generateSuitableFor = (productName: string): string[] => {
  const tags: string[] = [];
  const nameLower = productName.toLowerCase();

  if (nameLower.includes('acne') || nameLower.includes('pimple') || nameLower.includes('sali') || nameLower.includes('peroxide') || nameLower.includes('sebo') || nameLower.includes('zit') || nameLower.includes('clin')) {
    tags.push('Acne & Breakouts');
    tags.push('Oil Control & Sebum');
  }
  if (nameLower.includes('brighten') || nameLower.includes('radiance') || nameLower.includes('vitamin c') || nameLower.includes('kojic') || nameLower.includes('depigment') || nameLower.includes('glo') || nameLower.includes('azelaic') || nameLower.includes('melano') || nameLower.includes('lite')) {
    tags.push('Pigmentation');
  }
  if (nameLower.includes('hydra') || nameLower.includes('hyaluronic') || nameLower.includes('moistur')) {
    tags.push('Hydration Levels');
  }
  if (nameLower.includes('anti-aging') || nameLower.includes('retinol') || nameLower.includes('wrinkle') || nameLower.includes('firmness') || nameLower.includes('age')) {
    tags.push('Signs of Aging');
  }
  if (nameLower.includes('texture') || nameLower.includes('glycolic') || nameLower.includes('aha') || nameLower.includes('peel')) {
    tags.push('Skin Texture & Surface');
  }
  if (nameLower.includes('gentle') || nameLower.includes('sooth') || nameLower.includes('sensitive') || nameLower.includes('redness')) {
    tags.push('Redness & Sensitivity');
  }

  // Add a generic tag if no specific tags were found
  if (tags.length === 0) {
    tags.push('General Wellness');
  }

  return Array.from(new Set(tags)); // Return unique tags
};


// A function to parse CSV and create product objects
const parseAndEnrichProductsFromCSV = (csv: string): WebsiteProduct[] => {
  const lines = csv.trim().split('\n');
  const headersLine = lines.shift();
  if (!headersLine) return [];
  
  const headers = headersLine.split(',');

  const nameIndex = headers.indexOf('name');
  const linkIndex = headers.indexOf('link');
  const imageUrlIndex = headers.indexOf('image_url');
  const variantIdIndex = headers.indexOf('variant_id');

  if (nameIndex === -1 || linkIndex === -1 || imageUrlIndex === -1 || variantIdIndex === -1) {
    console.error("CSV headers are missing required columns: name, link, image_url, variant_id");
    return [];
  }

  const products: WebsiteProduct[] = [];

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];
    // Regex to handle commas inside quotes for product names.
    const values = line.match(/(".*?"|[^,]+)(?=\s*,|\s*$)/g) || [];
    
    if (values.length <= Math.max(nameIndex, linkIndex, imageUrlIndex, variantIdIndex)) continue;

    const name = (values[nameIndex] || '').replace(/^"|"$/g, '').trim();
    const url = (values[linkIndex] || '').trim();
    let imageUrl = (values[imageUrlIndex] || '').trim();
    const variantId = (values[variantIdIndex] || '').trim();

    if (!name || !url || !imageUrl || !variantId) continue;
    
    if (imageUrl.startsWith('//')) {
        imageUrl = `https:${imageUrl}`;
    }

    const urlParts = url.split('/');
    const handle = urlParts[urlParts.length - 1] || `product-${i}`;

    products.push({
      id: handle,
      name,
      url,
      imageUrl,
      description: generateDescription(name),
      keyIngredients: [], // This is left empty as it's not in the CSV data
      suitableFor: generateSuitableFor(name),
      variantId: variantId,
    });
  }
  return products;
};

export const DERMATICS_INDIA_PRODUCTS: WebsiteProduct[] = parseAndEnrichProductsFromCSV(csvData);